INSERT INTO `relapse_information` VALUES (1046, NULL, 399070, 1, '胸壁复发', '', '2015-01-14', '开放活检', '', '', '2015.1.26予TP方案（PTX 210mg d1 + CBP 500mg d1）*2+2015.5.25行减量TP方案4周期化疗，PTX 210mg d1，CBP 500mg d2；2015.6.9-2016.9予“来曲唑＋亮丙瑞林”维持内分泌治疗，联合伊维莫司；2017.2.16开始予GP方案化疗共5周期；唑来膦酸保骨', '');
INSERT INTO `relapse_information` VALUES (1047, NULL, 474641, 1, '', '', '2019-05-20', '粗针吸穿刺', '', '', '赫赛汀750mg（首次）540mg（维持）d1+帕妥珠单抗 840mg（首次）420mg（维持）d1+多西他赛 160mg d2）*8周期+双靶（赫赛汀750mg（首次）550mg（维持）d1+帕妥珠单抗 840mg（首次）420mg（维持）d1）*2周期，帕米磷酸二钠保骨', 'PD');
