INSERT INTO `surgical_pathological_info` VALUES (2941, NULL, 625397, '2020-12-11', '右乳癌是＋前哨淋巴结活检术', '是', '否', '前哨淋巴结活检', '', NULL, '', 'pTxN0（sn）Mx', '导管内癌', '（右）乳腺导管内癌，以粉刺型、实性型为主，核分Ⅱ—Ⅲ。本院切检后，是手术标本内未见残留癌组织，见灶性导管上皮不典型增生，平坦型上皮不典型性，导管上皮乳头状增生。', 'II-III', '', '', '', '', '—', '', '4号位散在数小灶导管上皮不典型增生，平坦型上皮不典型性，导管上皮增生及乳头状增生，柱状细胞增生；5号位散在数小灶导管上皮不典型增生，导管上皮增生，灶性导管上皮乳头状增生；6号位小叶不典型增生，小灶导管上皮不典型增生，导管上皮增生；7号位灶性导管上皮不典型增生；', '', '“前哨”淋巴结0/6（冰冻及冰余标本）', '6', '0', '90', '70', '2+', '', '30', '5', '', '', '', '');
