INSERT INTO `surgical_pathological_info` VALUES (3038, NULL, 646256, '2021-06-11', '右乳腺癌根治术I甲', '否', '否', '腋清', '', NULL, '', 'pT3N2aMx', '其他', '右乳腺中内浸润性导管癌，部分区伴浸润性微乳头状癌分化，部分组织学Ⅲ，部分组织学Ⅱ，癌组织累及脂肪，见广泛淋巴管癌栓，间质内浸润淋巴细胞约占5%；伴导管原位癌成分，粉刺型、实性型为主，核分Ⅲ（肿物一）；浸润性导管癌，组织学Ⅱ，癌组织累及脂肪，见广泛淋巴管癌栓，间质内浸润淋巴细胞约占5%；伴灶性导管原位癌成分，核分Ⅲ（肿物二）；瘤间腺体（+），见浸润性癌及淋巴管癌栓； \n乳头（-）；中上（-）；中下（-）； \n区域淋巴结：腋尖0/2；肌间0/0；腋下7/18+软组织（+）； \n病理学分期：pT3N2aMx ', 'II-III', '', '是', '有', '', '', '', '', '', '区域淋巴结：腋尖0/2；肌间0/0；腋下7/18+软组织（+）；', '6', '7', '85%/85%', '50%/15%', '2+', '', '50%/35% ', '5%/5%', '阴性', '', '60', '1.3');
