INSERT INTO `surgical_pathological_info` VALUES (3053, NULL, 641971, '2020-07-14', '右乳癌根治术', '否', '否', '腋清', '', NULL, '', 'ypT2N3Mx', '浸润性导管癌', '乳腺浸润性导管癌，间质内大量淋巴细胞浸润＞50%，组织学III，非特殊类型，脉管（+）、未见侵犯神经，侵犯脂肪组织；乳头（+）。肿物大小约3*2*1.6cm，肿瘤细胞内见坏死，简直内大量淋巴细胞浸润，符合化疗反应。区域淋巴结转移，分组如下：腋窝24/27（淋巴结内转移癌最大径7mm），腋静脉上0/0，腋尖2/2+见癌栓；病理分期ypT2N3Mx；', 'III', '', '', '', '', '', '', '', '', '腋窝24/27（淋巴结内转移癌最大径7mm），腋静脉上0/0，腋尖2/2+', '29', '24', '<1%', '<1%', '2+', '', '0.7', '0.9', '', '', '', '');
